body.script:
//
$(document).ready(function() {
    // 100ms aralıklarla Server yazısını kontrol et ve değiştir
    var checkServerText = setInterval(function() {
        // Sekme başlıklarındaki Server yazısını bul
        var serverTab = $(".ep-tab-button:contains('Server')");
        if (serverTab.length > 0) {
             serverTab.each(function() {
                // Sadece metin kısmını değiştir (ikonları korumak için)
                $(this).contents().filter(function() {
                    return this.nodeType == 3 && this.nodeValue.indexOf("Server") > -1;
                }).each(function(){
                    this.nodeValue = this.nodeValue.replace("Server", "Sunucular/Sezonlar");
                });
             });
        }

        // Eğer Server butonları (Server 1, Server 2 vb.) varsa onları da değiştir
        var serverBtns = $(".server-btn:contains('Server')");
        if (serverBtns.length > 0) {
             serverBtns.each(function() {
                $(this).contents().filter(function() {
                    return this.nodeType == 3;
                }).each(function(){
                    this.nodeValue = this.nodeValue.replace("Server", "Sunucular/Sezonlar");
                });
             });
        }

    }, 500); // Yarım saniyede bir kontrol eder

    // 10 saniye sonra kontrolü durdur (performans için)
    setTimeout(function() {
        clearInterval(checkServerText);
    }, 10000);
});
//

